INSERT INTO `additional_features_master` (`id`, `feature_content`, `feature_image`, `created_date`, `updated_date`, `status`) VALUES
(1, 'There are many sources of EPDM available, but Dinoflex only uses the best quality, featuring low odor and long-lasting color.', '20181024060821152.svg', '2018-10-24 06:03:30', '0000-00-00 00:00:00', 1),
(2, 'Dinoflex&rsquo;s Evolution Commercial Flooring is FloorScore Certified and guaranteed never to curl.', '2018102406084284.svg', '2018-10-24 06:08:42', '0000-00-00 00:00:00', 1),
(3, 'Evolution tiles are stain resistant and remarkably easy to maintain without the use of harsh chemicals.', '20181024060925420.svg', '2018-10-24 06:09:25', '0000-00-00 00:00:00', 1),
(4, 'These precision water-jet cut tiles are easy to install and come in straight edge, glued in place or a fully reversible interlocking format.', '20181024060959173.svg', '2018-10-24 06:09:59', '0000-00-00 00:00:00', 1),
(5, 'Our uniquely different recycled rubber flooring is long-lasting, fade resistant, and come in a variety of vibrant, rich, modern-looking colors.', '20181024061033414.svg', '2018-10-24 06:10:33', '0000-00-00 00:00:00', 1),
(6, 'Rubber has a memory and wants to return to its natural state; by manufacturing our materials in blocks opposed to rolls we guarantee the product is made flat, stays flat and guaranteed never to curl.&nbsp;', '20181024063755513.svg', '2018-10-24 06:37:55', '0000-00-00 00:00:00', 1),
(7, 'Dinoflex doesn&rsquo;t landfill waste from our manufacturing process, we recycle any excess materials back into this product to create an ultra-dense impact absorbing backing.', '20181024063831295.svg', '2018-10-24 06:38:31', '0000-00-00 00:00:00', 1),
(8, 'Get high performance toughness with sleek design options. Custom color capabilities provide endless options.', '20181024063900453.svg', '2018-10-24 06:39:00', '0000-00-00 00:00:00', 1),
(9, 'These precision water-jet cut tiles are easy to install and come straight edge and interlock.', '20181024063937497.svg', '2018-10-24 06:39:37', '0000-00-00 00:00:00', 1),
(10, 'Dinoflex&rsquo;s NEXT STEP&trade; High Impact Extreme is FloorScore Certified.', '20181024064143998.svg', '2018-10-24 06:41:43', '0000-00-00 00:00:00', 1),
(11, 'Choose from our wide range of colors or create your own to boast team pride.', '201810240642403.svg', '2018-10-24 06:42:40', '0000-00-00 00:00:00', 1),
(12, 'Offers maximum protection for your sub-floor so you don&rsquo;t have to worry about damage.', '20181024064302301.svg', '2018-10-24 06:43:02', '0000-00-00 00:00:00', 1),
(13, 'Dinoflex&rsquo;s NEXT STEP&trade; Luxury is FloorScore Certified.', '20181024065014496.svg', '2018-10-24 06:50:14', '0000-00-00 00:00:00', 1),
(14, 'Our uniquely different ergonomic features reduce joint pain from standing.', '20181024065043442.svg', '2018-10-24 06:50:43', '0000-00-00 00:00:00', 1),
(15, 'Recycled rubber surfacing has a simple maintenance program and a longer life than carpet, a product truly worth investing in.', '20181024065118273.svg', '2018-10-24 06:51:18', '0000-00-00 00:00:00', 1),
(16, 'Endless combinations available, create your own color using our Color Innovator.', '2018102406514514.svg', '2018-10-24 06:51:45', '0000-00-00 00:00:00', 1),
(17, 'Bond the color you desire to our recycled black backing, increasing the recycled content and lowering the cost.', '2018102406520993.svg', '2018-10-24 06:52:09', '0000-00-00 00:00:00', 1),
(18, 'A treatment of DinoCoat will help to further protect the cork granules once installed.', '20181024071536318.svg', '2018-10-24 07:15:36', '0000-00-00 00:00:00', 1),
(19, 'The combination of recycled rubber with cork results in an easy to maintain surface that is long lasting.', '20181024071601717.svg', '2018-10-24 07:16:01', '0000-00-00 00:00:00', 1),
(20, 'Dinoflex&rsquo;s NEXT STEP&trade; Walk Soft is FloorScore Certified.', '2018102407211316.svg', '2018-10-24 07:21:13', '0000-00-00 00:00:00', 1),
(21, 'Using our advanced lamination technology, we bond two layers into one, combining design and comfort for the ultimate product.', '20181024072139874.svg', '2018-10-24 07:21:39', '0000-00-00 00:00:00', 1),
(22, 'Less compression in the Walk Soft backing provides traction and additional cushioning, offering maximum comfort', '20181024072254106.svg', '2018-10-24 07:22:54', '0000-00-00 00:00:00', 1),
(23, 'Walk Soft is a uniquely different flooring perfect for standing for long periods.', '2018102407252466.svg', '2018-10-24 07:25:24', '0000-00-00 00:00:00', 1);
