INSERT INTO `cms_about` (`id`, `title`, `browsertitle`, `meta_description`, `description`, `image`, `icon`, `status`, `section`, `created`, `updated`) VALUES
(1, 'About Dinoflex', 'About Dinoflex  | Recycled Rubber Flooring Solutions ', 'Our unique manufacturing process produces an extraordinary range of splendidly colored flooring suitable for a massive amount of sport and commercial applications and can be tailored for elaborate logos and designs. We are pleased to offer a product that helps make the world a more habitable place.', '<div>\r\n<p>At Dinoflex, passionately doing what is uniquely different means developing solution driven innovations. For decades, flooring products have been manufactured out of various materials, regardless of their effects on human health, the environment, and even the spaces they occupy. We know offering unique solutions means doing things differently. By using one of the most sustainable materials available, we’ve developed a unique manufacturing process to create the world&#39;s highest quality “Made Flat, Stays Flat™\" - recycled rubber flooring system.</p>\r\n</div>\r\n', '20200821175814246.jpg', '20181003083434575.png', 1, 'section_1', '2018-10-03 08:07:47', '2021-03-03 01:26:16'),
(2, 'Our Brand', 'About us', NULL, '<span style=\"color:rgb(255, 255, 255); font-family:helvetica neue,helvetica,arial,sans-serif; font-size:18px\">For over 30 years we&rsquo;ve dedicated ourselves to passionately doing what is uniquely different to build a trusted flooring brand. Our proven and continued commitment to quality products and industry leading innovation inspires architects and designers to create spectacular spaces that they can be truly proud of.</span>', '20190524214821464.jpg', NULL, 1, 'section_2', '2018-10-03 08:44:09', '2019-11-21 09:02:22'),
(6, 'Safety Above All', 'About us', NULL, '<p>Dinoflex is laser focused on promoting a safe and healthy working environment for its employee&rsquo;s. A positive attitude toward safety at all levels is the goal of our company.</p>\r\n', '20181023120704301.svg', '20181023120704596.svg', 1, 'section_3', '2018-10-03 08:57:05', '2019-05-13 07:22:46'),
(5, 'Core Values', 'About us', NULL, 'Core Values are what support our vision, shape our culture and truly reflect what we value. Our core values are the essence of our identity, our principles, and our beliefs. It is through constant research and development and the tireless pursuit of innovation that we remain at the forefront of our industry. Read more about our values below.&nbsp;', '20181023120649134.svg', '20181023120650711.svg', 1, 'section_3', '2018-10-03 08:53:30', '2018-12-16 03:24:21'),
(7, 'Customer Intimacy', 'About us', NULL, '<p>Through on-going purposeful conversations and activities, Dinoflex Group is committed to building strong relationships with our customers. Our “Customer for Life” philosophy is at the forefront of everything we do.</p>\r\n', '20181023120734245.svg', '20181023120734240.svg', 1, 'section_3', '2018-10-03 08:58:27', '2019-05-13 07:24:17'),
(8, 'Business Innovation', 'About us', NULL, '<p>The mindful creativity toward new products and new processes is at the core of embracing and driving purposeful change.</p>', '20181023120750680.svg', '20181023120750411.svg', 1, 'section_3', '2018-10-03 08:59:33', '2019-05-13 07:26:59'),
(9, 'Extreme Ownership', 'About us', NULL, '<p>Our team is 100% accountable to be the best we can be, contributing, not detracting, to the overall success of the company.</p>\r\n', '20181023120808958.svg', '20181023120808905.svg', 1, 'section_3', '2018-10-03 09:00:54', '2019-11-21 09:03:15'),
(10, 'Quality Statement', 'About us', NULL, '<p>Dinoflex is proud to pledge our commitment to providing customers with products that will leave them 100 percent satisfied &ndash; without harming the planet.</p>\r\n\r\n<p>We achieve this result by focusing on innovation, research and sustainability.</p>\r\n', '20181023120826677.svg', '20181023120826484.svg', 0, 'section_3', '2018-10-03 09:48:25', '2019-05-13 07:34:37'),
(11, 'Vision', 'About us', NULL, '<ol>\r\n	<li><span style=\"font-size: 13px;\">To always reside at the forefront of recycled rubber manufacturing.</span></li>\r\n	<li>\r\n	<p class=\"western\" lang=\"en\" style=\"margin-bottom: 0cm; line-height: 150%\"><span style=\"font-size: 13px;\">To make breakthroughs in technology that will improve our products.</span></p>\r\n	</li>\r\n	<li>\r\n	<p class=\"western\" lang=\"en\" style=\"margin-bottom: 0cm; line-height: 150%\"><span style=\"font-size: 13px;\">To leave all of our customers completely satisfied each and every time.</span></p>\r\n	</li>\r\n</ol>', '20181003095008235.jpg', NULL, 1, 'section_4', '2018-10-03 09:50:08', NULL),
(16, 'Our Vision', 'About us', NULL, '<ol class=\"ol_margin\" style=\"font-family: helvetica neue,helvetica,arial,sans-serif;\r\n    font-size: 18px;\">\r\n	<li>To always reside at the forefront of recycled rubber manufacturing.</li>\r\n	<li>To make breakthroughs in technology that will improve our products.</li>\r\n	<li>To leave all of our customers completely satisfied each and every time.</li>\r\n</ol>\r\n', '20181124113852479.svg', '20181124113906882.svg', 0, 'section_3', '2018-11-13 11:22:27', '2019-07-10 12:00:50'),
(12, 'Dinoflex is proud to pledge our', 'About us', NULL, '<span style=\"color:rgb(255, 255, 255); font-family:helvetica neue,helvetica,arial,sans-serif; font-size:18px\">Consumers around the world generate well over one billion waste tires every year, many of which end up in landfills or are burned, endlessly polluting our precious land, air and water resources. Dinoflex has taken countless tires out of landfills and repurposed them into beautiful, durable, FloorScore&reg; certified flooring solutions that qualify for valuable LEED credits.</span>', '20181003095205769.jpg', NULL, 1, 'section_5', '2018-10-03 09:52:05', '2020-08-12 16:48:47'),
(13, 'Community Involvement', 'About us', NULL, '<div class=\"card border_none pb-4 pb-sm-4 pb-md-5 pb-lg-5 pb-xl-5\">\r\n<div class=\"card-body\">\r\n<h2 class=\"blue_title\">Community Involvement</h2>\r\n\r\n<p class=\"card-text pb-xl-3 pb-sm-0 small-txt\" style=\"    font-family: helvetica neue,helvetica,arial,sans-serif;\r\n    font-size: 18px;\">We proudly work with local and national charities and organizations that provide support and services to those in need.</p>\r\n</div>\r\n</div>\r\n', NULL, NULL, 1, 'section_6', '2018-10-03 09:53:02', '2020-01-03 23:23:34'),
(14, 'Vision', 'About us', NULL, '<div class=\"card bdr  mt-4 mt-sm-0 mt-md-0 mt-lg-0 mt-xl-0 pb-lg-2 pb-md-2 pb-xl-0\">\r\n<div class=\"card-body\">\r\n<h2 class=\"card-title blue-txt-b\"><i class=\"fa fa-eye\" style=\"font-size: 1.65rem;background: none;color: #003968;\"></i>&nbsp;Our Vision</h2>\r\n\r\n<ol class=\"ol_margin\" style=\"font-family: helvetica neue,helvetica,arial,sans-serif;\r\n    font-size: 18px;\">\r\n	<li>To always reside at the forefront of recycled rubber manufacturing.</li>\r\n	<li>To make breakthroughs in technology that will improve our products.</li>\r\n	<li>To leave all of our customers completely satisfied each and every time.</li>\r\n</ol>\r\n</div>\r\n</div>\r\n', '20181113111805277.svg', '20181113111805549.svg', 0, 'section_6', '2018-10-03 09:53:32', '2018-11-13 11:24:36'),
(15, 'Community Involvement', 'Community Involvement', 'We are proud to work with various charities and organizations active in the community who lend support and services to various groups in need. We believe that it is essential for businesses to be actively involved to foster a strong community for everyone.', '<span xss=\"removed\">We are proud to work with various charities and organizations active in the community who lend support and services to various groups in need. We believe that it is essential for businesses to be actively involved to foster a strong community for everyone.</span>', '20190903200444743.jpg', NULL, 1, 'section_7', '2018-10-03 11:54:57', '2021-03-03 01:35:00');
