INSERT INTO `products` (`id`, `product_name`, `slug`, `product_category`, `meta_description`, `banner_image`, `product_list_banner`, `header_title`, `header_sub_title`, `browsertitle`, `header_content`, `thumbnail_content`, `mid_title`, `mid_content`, `features`, `additional_features`, `benefits`, `applications`, `size_content`, `video_image_1`, `video_link_1`, `video_image_2`, `video_link_2`, `video_image_3`, `video_link_3`, `order_index`, `created_date`, `updated_date`, `status`, `features_1`, `applications_1`) VALUES
(2, 'NEXT STEP™ Walk Soft - The Prescription for Comfort ', 'walk-soft', 1, '', '20190613063603603.png', '20191001174728681.jpg', 'NEXT STEP™ Walk Soft -  ', 'The Prescription for Comfort', 'NEXT STEP™ Walk Soft - The Prescription for Comfort ', '<br>\r\nWhen aesthetics and ergonomics both come first, we recommend NEXT STEP™ Walk Soft recycled rubber flooring. Choose from custom or curated colors to suite your design and we’ll create a single ergonomic surface with our Walk Soft backing for the ultimate underfoot comfort and support. With NEXT STEP™ Walk Soft you’ll enjoy the look AND feel of your floor for years to come.', 'High color design and ergonomics are the main features that make up our Next Step Walk Soft product series.', 'Created for Underfoot Comfort', 'This series offers a softer, more comfortable feel while still giving users the durability and easy maintenance that makes recycled rubber flooring so versatile and long lasting. Walk Soft reduces stress on back, knee, hip and ankle joints, reducing costly health claims.', '26,2,22,23,25,24,21', '', '<ul class=\"list-group-flush product_list_resposive square_list_type\">\r\n <li class=\"product-group \">\r\n <p>Walk Soft flooring eliminates the need for additional anti-fatigue mats at workstations</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Reduced fall Consequences</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Prevents unsightly stains that can affect how your environment is viewed</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Results in low maintenance saving money and time</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Will not amplify sounds, reducing a distracting and unappealing environment</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Utilizing recycled content reduces the overall costs</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Long product life</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Walk Soft backing can be applied to any Dinoflex color</p>\r\n </li>\r\n</ul>\r\n', '15,28,19,10,16,17,20,18', '<div class=\"col-md-6\">\r\n<p><img alt=\"Interlock Tile\" src=\"https://dinoflex.com/frontside/images/interlocking_floor.png\"><strong>Interlocking Floor (Loose Lay)</strong></p>\r\n\r\n<ul>\r\n <li>37” x 37” = 9.5ft?2; (94cm x 94cm = 0.88m?2;)</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"col-md-6\">\r\n<p><img alt=\"Interlock Tile\" src=\"https://dinoflex.com/frontside/images//square_edge.png\"><strong>Square Edge Tile (Glue Down)</strong></p>\r\n\r\n<ul>\r\n <li>38” x 38” = 10.0ft?2; (96.5cm x 96.5cm = 0.93m?2;)</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"col-md-7\">\r\n<p><strong>Standard Thickness</strong></p>\r\n\r\n<ul>\r\n <li>8mm – 5/16” (4mm top surface with 4mm recycled black backing)</li>\r\n <li>10mm – 3/8” (4mm top surface with 6mm recycled black backing)</li>\r\n <li>Tolerance of +/- 0.5mm</li>\r\n</ul>\r\n* C<span xss=\"removed\">ustom thicknesses are available.</span></div>\r\n', 'Image.jpg', '', 'Image.jpg', '', 'Image.jpg', '', 4, '2018-10-04 14:28:35', '2021-02-22 11:59:38', 1, '<div class=\"col-xl-6 col-sm-6\">\r\n<ul class=\"list-group-flush product_list_resposive square_list_type\">\r\n <li class=\"product-group \">\r\n <p>Superior ergonomic qualities</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>High Traction</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Stain resistant</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Easy cleaning</p>\r\n </li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"col-xl-6 col-sm-6\">\r\n<ul class=\"list-group-flush product_list_resposive square_list_type\">\r\n <li class=\"product-group \">\r\n <p>Sound reducing</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>High level of recycled content</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>High density top layer</p>\r\n </li>\r\n <li class=\"product-group \">\r\n <p>Unlimited color selection</p>\r\n </li>\r\n</ul>\r\n</div>\r\n', NULL),
(3, 'Walk Soft Underlayment', 'walk-soft-underlayment', 1, NULL, '20190613060549797.png', '20200102202348830.jpg', 'Walk Soft Underlayment', '', 'Walk Soft Underlayment', '<h2>Made Flat Stays Flat</h2>\r\nSound reduction, and thermal insulation are just a few of the benefits of using Dinoflex&nbsp; WalkSoft Underlayment. Manufactured from&nbsp; recycled rubber, these tiles are a fantastic alternative to other rolled underlayment or costly sound engineering systems.', 'Sound reduction, thermal insulation and underfoot comfort are just a few of the benefits of using Dinoflex Walksoft Underlayement.', 'Affordable and Easy to Install', 'These 4&rsquo; x 6&rsquo; mats are primarily used under hardwood and engineered wood flooring to achieve superior sound reduction, creating a better interior living space and quieter work environment. I think the underlayment can be used under more than just hardwood and engineered wood.', '28,27,29,25', '', '', '11,14', '<ul style=\"list-style-type:square\">\r\n	<li>39.5&rdquo; x 39.5&rdquo; (100 cm x 100 cm) 10.83 sq.ft./1 sq. meter</li>\r\n	<li>1/8&rdquo; (3 mm)</li>\r\n	<li>5/32&rdquo; (4 mm)</li>\r\n	<li>3/16&rdquo; (5 mm)</li>\r\n	<li>1/4&rdquo; (6 mm)</li>\r\n</ul>\r\n', 'Image.jpg', '', NULL, '', NULL, '', 9, '2018-10-05 09:25:07', '2020-09-16 04:18:04', 0, '<div class=\"row\">\r\n<div class=\"col-xl-6 col-sm-6\">\r\n<ul class=\"list-group-flush product_list_resposive square_list_type\">\r\n	<li class=\"product-group \">\r\n	<p>Durable</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>High resiliency</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>Post-Consumer Recycled Content</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>Sound reducing</p>\r\n	</li>\r\n</ul>\r\n</div>\r\n</div>\r\n', NULL),
(4, 'CushionWalk® Pavers - A safer and More Comfortable Walking Surface', 'cushion-walk-pavers', 2, NULL, '20190613064300853.png', '20190610231526481.jpg', 'CushionWalk® Pavers - ', 'Enjoy the outdoors comfortably.', 'CushionWalk® Pavers - A safer and More Comfortable Walking Surface', '<h2>&nbsp;</h2>\r\nBuild the path to better outdoor living with CushionWalk &reg; Pavers. Forget hard concrete and stone tiles; beautify patios, balconies and walkways with elegant paver tiles that create a safer and more comfortable outdoor surface. With a variety of colors to choose from we&rsquo;ve reimagined outdoor living to look and feel better with our ergonomic and attractive pavers.', 'These resilient, long lasting tiles are the ideal flooring product for patios, balconies walkways and more.', 'Resilient Long-Lasting Tiles', 'For a safe and more comfortable walking surface our &frac34;&rdquo; flat back tiles are impact absorbing reducing strain from standing and injuries caused by falling. Renew old surfaces and add a touch of elegance with our smooth top or decorative brick pattern options.', '34,2,4,31,33,1,32,44', '', '', '26,24,25,23,21,22', '<div class=\"col-md-5\"><img alt=\"Interlock Tile\" src=\"https://dinoflex.com/frontside/images/square_edge.png\" /><strong>Square Edge Tile</strong>\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>23 &frac34;&rdquo; x 23 &frac34;&rdquo; = 3.92 ft&sup2; (60 cm x 60 cm = 0.36 m&sup2;)</li>\r\n</ul>\r\n\r\n<p><strong>Standard Thickness</strong></p>\r\n\r\n<ul>\r\n	<li>3/4&rdquo; (19 mm)</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"col-md-4\"><img alt=\"Interlock Tile\" src=\"https://dinoflex.com/frontside/images/interlocking_tile.png\" /><strong>Interlocking Tile</strong>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>24&rdquo; x 24&rdquo; 4ft&sup2; (61 cm x 61 cm = 0.37 m&sup2;)</li>\r\n</ul>\r\n\r\n<p><strong>Standard Thickness</strong></p>\r\n\r\n<ul>\r\n	<li>3/4&rdquo; (19 mm)</li>\r\n</ul>\r\n</div>\r\n', NULL, '', NULL, '', NULL, '', 17, '2018-10-05 12:28:41', '2020-06-04 12:25:09', 0, '<div class=\"row\">\r\n<div class=\"col-xl-6 col-sm-6\">\r\n<ul class=\"list-group-flush product_list_resposive square_list_type\">\r\n	<li class=\"product-group \">\r\n	<p>Easy to install and low maintenance</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>High Traction</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>Impact Absorbing</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>Indent Resistant</p>\r\n	</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"col-xl-6 col-sm-6\">\r\n<ul class=\"list-group-flush product_list_resposive square_list_type\">\r\n	<li class=\"product-group \">\r\n	<p>Loose lay interlocking or glue down applications</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>Noise reducing</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>Smooth or brick top pattern</p>\r\n	</li>\r\n	<li class=\"product-group \">\r\n	<p>Superior sound and impact absorption</p>\r\n	</li>\r\n</ul>\r\n</div>\r\n</div>\r\n', NULL),
(5, 'Custom Floor Design And Logos - Let your imagination run wild!', 'custom-floor-design-and-logos', 3, NULL, '20190619221719219.png', '20190913154045129.jpg', 'Custom Capabilities - ', 'Let your imagination run wild!', 'Custom Floor Design And Logos - Let your imagination run wild!', '<br />\r\nWe have been a leader in the flooring industry for over 30 years, continually pushing the limits of what can be done with color, design, and our environmentally sustainable material of choice &ndash; rubber. Recycled rubber is so extremely versatile that our customers can incorporate their vivid color designs, custom logos and artwork into their projects with ease. Providing the highest quality product with the lowest minimums is something we are extremely proud of.', '<span style=\"font-size: 13px;\">Our precision water cutter technology enables us to create rubber floors that.</span><br />\r\n&nbsp;', 'Custom Logo Design', 'Dinoflex utilizes solid EPDM mats consisting of coarse and fine granules that are precision-cut with a waterjet cutter to create complex, dimensional logos. Utilize your valuable floor space by displaying company logos or brand elements throughout your facility. High color interactive mats can be used to create play and learning areas for children. The potential is unlimited when it comes to size and shape of logos, choose from over 26 colors to capture the essence of your brand.&nbsp; &nbsp;&nbsp;', '', '', '', '', '', NULL, '', NULL, '', NULL, '', 1, '2018-10-05 13:38:38', '2020-09-14 23:00:41', 1, '', NULL),
(25, 'Rubber King', 'rubber-king', 5, NULL, '20190524060416653.png', NULL, '', NULL, 'Rubber King', '', '', '', '', '', '', '', '', '', NULL, '', NULL, '', NULL, '', 16, '2018-11-20 19:46:37', '2019-06-11 07:06:13', 0, '', ''),
(6, 'Evolution Commercial Flooring - The Next Generation In Design.', 'evolution-commercial', 1, 'Commercial Flooring that allows a modern touch for office, restaurants and public faciltities. Rubber Flooring Tiles for your new project', '20190613063359188.png', '20200102200908667.jpg', 'Commercial Flooring - ', 'Evolution Commercial', 'Commercial Flooring | Rubber Flooring | Flooring Options', '<br>\r\nCommercial flooring has evolved from traditional carpet and hard surfaces to our luxurious Evolution recycled rubber flooring. Designers can bring their visions to life with brilliant colors creating a space that is cohesive and functional while standing the test of time. When you combine these striking color blends with high traction, underfoot comfort and superior sound reducing properties, this hardwearing, sustainable rubber flooring is perfect for a wide range of commercial applications. Experience the next generation in design.', 'When you combine these striking color blends with high traction, underfoot comfort and superior sound reducing properties, this hardwearing, sustainable recycled rubber tile flooring is perfect for a wide range of commercial and institutional applications.', 'Design and Durability Converge', 'Evolution Commercial Flooring is durable and attractive, and made with the best quality EPDM to ensure low odor and maximum color vibrancy. With over 70 colors to choose from, we have a unique solution to suit any design.', '94,96,41,39,98,97,99', '', '<ul>\r\n <li>\r\n <p>Ensures a healthy breathing environment</p>\r\n </li>\r\n <li>\r\n <p>Reduces risk of slip and falls</p>\r\n </li>\r\n <li>\r\n <p>Environmentally friendly and helps to contribute to the Green credit program</p>\r\n </li>\r\n <li>\r\n <p>A durable product that won’t show wear patterns and is guaranteed never to curl</p>\r\n </li>\r\n <li>\r\n <p>Makes for a quieter space</p>\r\n </li>\r\n <li>\r\n <p>Reduces the effect of stress on knees, hips, back and feet caused by prolonged