INSERT INTO `testimonial_slider` (`id`, `slider_image`, `title`, `upload`, `tag_line`, `status`, `created_datetime`, `modified_datetime`) VALUES
(2, '20181017051609345.jpg', 'Ann Arbor Ice Arena', '', '&ldquo;I hope you continue to make these mats because I plan on continuing to purchase them.&rdquo;<br />\r\n<br />\r\n- Diane Wilson, General Manager, Ann Arbor Ice Cube', 1, '2018-10-17 05:15:40', '2018-11-16 11:40:50'),
(3, '20181017052511197.jpg', 'H. Morrison, Floor Space', '', '&ldquo;After 2 seasons of everything that up to 4000 skiers a day can throw at it, the Dinoflex is not showing any wear at all and looks fantastic - a credit to your product!<br />\r\n<br />\r\n- H. Morrison, Floor Space', 1, '2018-10-17 05:23:41', '2018-11-16 11:41:56'),
(4, '20181017053122866.jpg', 'Fitness One Vega Family', '', '&quot;On behalf of Kazem and the Fitness One Vega Family, we extend our appreciation to all the support and cooperation you have extended throughout this project.&rdquo;<br />\r\n<br />\r\n- Samir K Ibrayghith, Managing Partner, IFST', 1, '2018-10-17 05:31:22', '2018-12-15 23:06:12'),
(5, '2018101705335093.jpg', 'Focus on Dinoflex - Vancouver, BC', '', '&ldquo;It&rsquo;s a great product - durable, sustainable, easy to maintain and attractive.&rdquo;<br />\r\n<br />\r\n- Kevin Hanvey MAIBC, Principal &amp; Director of Sustainability, Omicron Architecture', 1, '2018-10-17 05:32:08', '2018-11-16 11:43:04'),
(6, '20181017053323268.jpg', 'OK College, Penticton BC', '', '&ldquo;The use of Dinoflex flooring was a great fit for the Centre of Excellence project: a product manufactured locally with high recycled content.&rdquo;<br />\r\n<br />\r\n- Robert Parlane, CEI Architecture Planning Interiors', 1, '2018-10-17 05:33:23', '2018-11-16 11:43:26'),
(7, '20181017053509261.jpg', 'Home Gym - Smithers, BC', '', '&quot;What a great product - easy to work (cut, lock and place). Thanks for the great product.&quot;<br />\r\n<br />\r\n&ndash; Home Gym, Smithers BC', 1, '2018-10-17 05:35:09', '2019-05-13 11:45:33'),
(8, '2018101705363697.jpg', 'MWR Fitness - Yokosuka, Japan', '', '&quot;The Dinoflex flooring has been an excellent upgrade to our fitness facility, as It was easy to install, and immediately transformed the professional look of our gym.&rdquo;<br />\r\n<br />\r\n- Kyle Rhodus, Athletic Director, MWR, Yokosuka, Japan', 1, '2018-10-17 05:36:36', '2018-11-16 11:44:30'),
(9, '20181017053728134.jpg', 'Springbank Park - Calgary, AB', '', '&quot;After the initial installation in 2002 of our lobby and arena areas, we have since ordered more Dinoflex products that now cover our players&#39; rooms, preschool, hallways, offices and more.&rdquo;<br />\r\n<br />\r\n- J. Rop, Springbank Park for All Seasons', 1, '2018-10-17 05:37:28', '2018-11-16 11:44:52'),
(10, '20181017053842696.jpg', 'Sunset High School - Portland, OR', '', '&ldquo;Dinoflex Rubber Flooring has proven to be sustainable flooring that lasts!&rdquo;<br />\r\n<br />\r\n- Mike Mitchell, Varsity Football Coach, Sunset High School, Portland Oregon', 1, '2018-10-17 05:38:42', '2018-11-16 11:45:14'),
(11, '20181017053945811.jpg', 'Whistler Blackcomb Ski Resort - Whistler, BC', '', '&quot;The Whistler Blackcomb Building Maintenance Department is pleased to recommend the use of Dinoflex Sport Mat for ski facilities.&rdquo;<br />\r\n<br />\r\n- Chad Wood, Maintenance Supervisor, Whistler Blackcomb', 1, '2018-10-17 05:39:45', '2018-11-16 11:45:34');
